import React, { useState } from 'react';
import SIgn_img from './SIgn_img';
import { useNavigate } from 'react-router-dom';
import "../styles/login"

const Login = () => {
    const history = useNavigate();

    const [inpval, setInpval] = useState({
        email: "",
        password: ""
    });

    console.log(inpval);

    const getdata = (e) => {
        const { value, name } = e.target;

        setInpval(() => {
            return {
                ...inpval,
                [name]: value
            }
        });
    };

    const login = () => {
        const { email, password } = inpval;

        if (email === "") {
            alert('Email field is required');
        } else if (!email.includes("@")) {
            alert('Please enter a valid email address');
        } else if (password === "") {
            alert('Password field is required');
        } else if (password.length < 5) {
            alert('Password length must be greater than five');
        } else {
            const getuserArr = localStorage.getItem("useryoutube");

            if (getuserArr && getuserArr.length) {
                const userdata = JSON.parse(getuserArr);
                const userlogin = userdata.filter((el, k) => {
                    return el.email === email && el.password === password
                });

                if (userlogin.length === 0) {
                    alert("Invalid details");
                } else {
                    console.log("User login successful");
                    localStorage.setItem("user_login", JSON.stringify(userlogin));
                    history("/details");
                }
            }
        }
    };

    return (
        <>
            <div className="container mt-3">
                <section className='d-flex justify-content-between'>
                    <div className="left_data mt-3 p-3" style={{ width: "100%" }}>
                        <h3 className='text-center col-lg-6'>Sign IN</h3>
                        <div className="mb-3 col-lg-6">
                            <label htmlFor="email" className="form-label">Email</label>
                            <input type="email" className="form-control" name='email' onChange={getdata} placeholder="Enter email" />
                        </div>
                        <div className="mb-3 col-lg-6">
                            <label htmlFor="password" className="form-label">Password</label>
                            <input type="password" className="form-control" name='password' onChange={getdata} placeholder="Password" />
                        </div>
                        <p className='mt-3'>Already Have an Account <span>SignIn</span> </p>
                    </div>
                    <SIgn_img />
                </section>
                <button onClick={login} style={{ background: "rgb(67, 185, 127)" }}>
                    Login
                </button>
            </div>
        </>
    )
}

export default Login;
