import React, { useState } from 'react';
import SIgn_img from './SIgn_img';
import { NavLink } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';

const Home = () => {
    const history = useNavigate();

    const [inpval, setInpval] = useState({
        name: "",
        email: "",
        date: "",
        password: ""
    });

    const getdata = (e) => {
        const { value, name } = e.target;

        setInpval(() => {
            return {
                ...inpval,
                [name]: value
            }
        });
    };

    const addData = (e) => {
        e.preventDefault();

        // Your validation logic here...

        console.log("data added successfully");
        history("/login");
        // Store data in localStorage or send it to your backend, as needed.
    };

    return (
        <>
            <div className="container mt-3">
                <section className='d-flex justify-content-between'>
                    <div className="left_data mt-3 p-3" style={{ width: "100%" }}>
                        <h3 className='text-center col-lg-6'>Sign Up</h3>
                        <div className="mb-3 col-lg-6">
                            <label htmlFor="name" className="form-label">Name</label>
                            <input type="text" className="form-control" name='name' onChange={getdata} placeholder="Enter Your Name" />
                        </div>
                        <div className="mb-3 col-lg-6">
                            <label htmlFor="email" className="form-label">Email</label>
                            <input type="email" className="form-control" name='email' onChange={getdata} placeholder="Enter email" />
                        </div>
                        <div className="mb-3 col-lg-6">
                            <label htmlFor="date" className="form-label">Date</label>
                            <input type="date" className="form-control" name='date' onChange={getdata} />
                        </div>
                        <div className="mb-3 col-lg-6">
                            <label htmlFor="password" className="form-label">Password</label>
                            <input type="password" className="form-control" name='password' onChange={getdata} placeholder="Password" />
                        </div>
                        <p className='mt-3'>Already Have an Account <span><NavLink to="/login">SignIn</NavLink></span> </p>
                    </div>
                    <SIgn_img />
                </section>
            </div>
        </>
    )
}

export default Home;
